package linkedlist_problems;

public class Node {

	int val;
	Node next;

	public Node(int val) {
		this.val = val;
		this.next = null;
	}

	public Node() {
		// TODO Auto-generated constructor stub
	}

	public int getVal() {
		return val;
	}

	public void setVal(int val) {
		this.val = val;
	}

	public Node getNext() {
		return next;
	}

	public void setNext(Node next) {
		this.next = next;
	}

}
